import './App.css';

import React, { useState } from 'react';

function Title() {
  const [margin, setMargin] = useState(0);
  let enterTimeout;
  let leaveTimeout;

  const handleMouseEnter = () => {
    enterTimeout = setTimeout(() => {
      if (margin < 10) {
        setMargin(prevMargin => prevMargin + 1);
        handleMouseEnter();
      }
    }, 100);
  };

  const handleMouseLeave = () => {
    leaveTimeout = setTimeout(() => {
      if (margin > 0) {
        setMargin(prevMargin => prevMargin - 1);
        handleMouseLeave();
      }
    }, 100);
  };

  const handleMouseEnterClear = () => {
    clearTimeout(enterTimeout);
  };

  const handleMouseLeaveClear = () => {
    clearTimeout(leaveTimeout);
  };

  return (
    <div
      className="isolate titles"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onMouseEnterCapture={handleMouseEnterClear}
      onMouseLeaveCapture={handleMouseLeaveClear}
    >
      <a href="index.html" id="title" className="title rainbow blue" style={{ marginLeft: -margin + 'px' }}>
        JackSavini.com
      </a>

      <a href="index.html" id="title" className="title rainbow green">
        JackSavini.com
      </a>

      <a href="index.html" id="title" className="title rainbow red" style={{ marginLeft: margin + 'px' }}>
        JackSavini.com
      </a>
    </div>
  );
}

function App() {
  return (
    <Title/>
  );
}

export default App;
