import './App.css';

import React, { useState } from 'react';

function Title() {
  const [margin, setMargin] = useState(0);
  let enterInterval;
  let leaveInterval;
  let mouseIn = false;

  const interval = setInterval(() => {
    if(mouseIn){
      setMargin(margin + 1)
    }
    else{
      setMargin(margin - 1)
    }
    setMargin(prevMargin => {
      if (prevMargin < 10) {
        return prevMargin + 1;
      }
      clearInterval(enterInterval)
      return 10;
    });
  }, 10);

  const handleMouseEnter = () => {
    mouseIn = true;
  };

  const handleMouseLeave = () => {
    mouseIn = false;
  };

  return (
    <div className="isolate titles" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <a href="index.html" id="title" className="title rainbow blue" style={{ marginLeft: -margin + 'px' }}>
        JackSavini.com
      </a>

      <a href="index.html" id="title" className="title rainbow green">
        JackSavini.com
      </a>

      <a href="index.html" id="title" className="title rainbow red" style={{ marginLeft: margin + 'px' }}>
        JackSavini.com
      </a>
    </div>
  );
}

function App() {
  return (
    <Title/>
  );
}

export default App;
