import './App.css';

import React, { useState } from 'react';

function Title() {
  const [margin, setMargin] = useState(0);
  var interval

  const handleMouseEnter = () => {
    clearInterval(interval);
    interval = setInterval(() => {
      if (margin < 5) {
        setMargin(margin => margin + 1);
        console.log(margin)
      } 
      if(margin >= 5) {
        clearInterval(interval);
      }
    }, 10);
  };

  const handleMouseLeave = () => {
    clearInterval(interval);
    interval = setInterval(() => {
      if (margin > 0) {
        setMargin(margin => margin - 1);
      } else {
        clearInterval(interval);
      }
    }, 10);
  };

  return (
    <div className="isolate titles" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <a href="index.html" id="title" className="title rainbow blue" style={{ marginLeft: -margin + 'px' }}>
        JackSavini.com
      </a>

      <a href="index.html" id="title" className="title rainbow green">
        JackSavini.com
      </a>

      <a href="index.html" id="title" className="title rainbow red" style={{ marginLeft: margin + 'px' }}>
        JackSavini.com
      </a>
    </div>
  );
}

function App() {
  return (
    <Title/>
  );
}

export default App;
