import './App.css';

import React, { useState } from 'react';

function Title() {
  const [margin, setMargin] = useState(0);
  let enterInterval;
  let leaveInterval;

  const handleMouseEnter = () => {
    clearInterval(leaveInterval); // Clear leaveInterval if it's running
    enterInterval = setInterval(() => {

      setMargin(margin => margin + 1);
      if (margin >= 10) {clearInterval(enterInterval);} // Clear interval when margin reaches 10

    }, 10);
  };

  const handleMouseLeave = () => {
    clearInterval(enterInterval); // Clear enterInterval if it's running
    leaveInterval = setInterval(() => {
      if (margin > 0) {
        setMargin(margin => margin - 1);
      } else {
        clearInterval(leaveInterval); // Clear interval when margin reaches 0
      }
    }, 10);
  };

  return (
    <div className="isolate titles" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <a href="index.html" id="title" className="title rainbow blue" style={{ marginLeft: -margin + 'px' }}>
        JackSavini.com
      </a>

      <a href="index.html" id="title" className="title rainbow green">
        JackSavini.com
      </a>

      <a href="index.html" id="title" className="title rainbow red" style={{ marginLeft: margin + 'px' }}>
        JackSavini.com
      </a>
    </div>
  );
}

function App() {
  return (
    <Title/>
  );
}

export default App;
